INSERT INTO software VALUES('malware--f9b05f33-d45d-4e4d-aafe-c208d38a0080','Azorult',replace('[Azorult](https://attack.mitre.org/software/S0344) is a commercial Trojan that is used to steal information from compromised hosts. [Azorult](https://attack.mitre.org/software/S0344) has been observed in the wild as early as 2016.\nIn July 2018, [Azorult](https://attack.mitre.org/software/S0344) was seen used in a spearphishing campaign against targets in North America. [Azorult](https://attack.mitre.org/software/S0344) has been seen used for cryptocurrency theft. (Citation: Unit42 Azorult Nov 2018)(Citation: Proofpoint Azorult July 2018)','\n',char(10)),'2019-01-30 15:19:14.309000','2020-03-30 14:56:50.733000','1.2',NULL,0);
