INSERT INTO software VALUES('malware--d5268dfb-ae2b-4e0e-ac07-02a460613d8a','BONDUPDATER','[BONDUPDATER](https://attack.mitre.org/software/S0360) is a PowerShell backdoor used by [OilRig](https://attack.mitre.org/groups/G0049). It was first observed in November 2017 during targeting of a Middle Eastern government organization, and an updated version was observed in August 2018 being used to target a government organization with spearphishing emails.(Citation: FireEye APT34 Dec 2017)(Citation: Palo Alto OilRig Sep 2018)','2019-02-18 20:16:12.119000','2020-03-30 15:22:05.356000','1.2',NULL,0);
