INSERT INTO software VALUES('malware--1f6e3702-7ca1-4582-b2e7-4591297d05a8','Bankshot','[Bankshot](https://attack.mitre.org/software/S0239) is a remote access tool (RAT) that was first reported by the Department of Homeland Security in December of 2017. In 2018, [Lazarus Group](https://attack.mitre.org/groups/G0032) used the [Bankshot](https://attack.mitre.org/software/S0239) implant in attacks against the Turkish financial sector. (Citation: McAfee Bankshot)','2018-10-17 00:14:20.652000','2020-03-30 20:41:17.223000','1.1',NULL,0);
