INSERT INTO software VALUES('malware--1b9f0800-035e-4ed1-9648-b18294cc5bc8','CARROTBAT','[CARROTBAT](https://attack.mitre.org/software/S0462) is a customized dropper that has been in use since at least 2017. [CARROTBAT](https://attack.mitre.org/software/S0462) has been used to install [SYSCON](https://attack.mitre.org/software/S0464) and has infrastructure overlap with [KONNI](https://attack.mitre.org/software/S0356).(Citation: Unit 42 CARROTBAT November 2018)(Citation: Unit 42 CARROTBAT January 2020)','2020-06-02 14:11:40.581000','2020-06-15 15:13:27.660000','1.0',NULL,0);
